import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import Loader from "../Common/Loader";

const ShowUser = () => {
  const showUserApi = "http://localhost:3001/user";
  const [user, setUser] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const handelDelete = async (id) => {
    console.log("id : -", id);

    const confirmAlert = window.confirm("Do you want to delete this user?");

    if(confirmAlert) {
      setIsLoading(true);
      try {
        const response = await fetch(showUserApi.concat("/") + id, {
          method: "DELETE",
        });
        if (!response.ok) {
          const errorText = await response.text();
          throw new Error(`Failed to delete item: ${response.status} ${response.statusText}. ${errorText}`);
        }
        const result = await response.json();
        console.log("Delete result:", result);
        setUser(user.filter((item) => item.id !== id));
      } catch (error) {
        console.error("Delete error:", error);
        setError(error.message);
      } finally {
        setIsLoading(false);
      }
    }
  };

  useEffect(() => {
    getUsers();
  }, []);

  const getUsers = () => {
    axios
      .get(showUserApi)
      .then((res) => {
        setUser(res.data);
      })
      .catch((err) => {
        console.error("Error fetching users:", err);
        setError("Failed to fetch users");
      });
  };

  if (user.length < 0) {
    return <h1>no user found</h1>;
  } else {
    return (
      <div className="mt-5">
        {isLoading && <Loader />}
        {error && <p>Error: {error}</p>}
        <table className="table table-striped">
          {/* ... 테이블 헤더 ... */}
          <tbody>
            {user?.map((item, i) => {
              return (
                <tr key={i + 1}>
                  {/* ... 다른 테이블 셀 ... */}
                  <td>
                    <Link to={`/edit-user/${item.id}`}>
                      <i className="fa fa-pencil" aria-hidden="true"></i>
                    </Link>
                    <Link to={`/user/${item.id}`}>
                      <i className="fa fa-eye" aria-hidden="true"></i>
                    </Link>
                    <i
                      className="fa fa-trash-o"
                      aria-hidden="true"
                      onClick={() => handelDelete(item.id)}
                    ></i>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    );
  }
};

export default ShowUser;
